%
%
figure
plot(Case_c_2(:,1),log10(Case_c_2(:,2)),'-k','LineWidth',1)
hold on
plot(Case_c_d_1(:,1),log10(Case_c_d_1(:,2)),'-b','LineWidth',1)

legend({'$c=2$','$c=d-1$'},...
        'FontSize',18,'Interpreter','latex','Location','northeast');

xlabel('{$d$}','FontSize',18,'Interpreter','latex');
ylabel('$\log_{10}$(Relative error)','FontSize',18,'Interpreter','latex');
set(gca,'XTick',[2:1:10],'FontSize',18);
set(gca,'YTick',[-15:1:-12]);
axis([2 10 -15 -12])
grid(gca,'minor')
grid on
set(gca, 'XMinorTick','on')
set(gca, 'YMinorTick','on')
ax=gca;
ax.GridAlpha=0.75;
ax.MinorGridAlpha=0.75;
set(gca,'LooseInset',get(gca,'TightInset'))
